sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

	function loadListItems() {
		c.server.update()
	}

	loadListItems();
};

sp_widget.css:
.skeletonText {
	height: 1.2rem;
  margin: .5em 0;
  background-color: $panel-default-border;
  opacity: 0%;

  animation: fade 2s infinite;
}

.skeletonText.title {
	width: 33.33%
}

@keyframes fade {
  from {
    opacity: 100%;
  }

  50% {
    opacity: 0%;
  }

  to {
    opacity: 100%;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	function getListItems() {
		var items = [];
		var gr = new GlideRecord('incident');
		gr.setLimit(5);
		gr.addEncodedQuery('active=true');
		gr.query();

		while (gr.next()) {
			items.push({
				'title': {
					'value': gr.getValue('number'),
					'display_value': gr.getDisplayValue('number')
				},
				'short_description': {
					'value': gr.getValue('short_description'),
					'display_value': gr.getDisplayValue('short_description')	
				},
				'meta': {
					'field_name': gr.getLabel('assigned_to'),
				  'value': gr.getValue('assigned_to'),
					'display_value': gr.getDisplayValue('assigned_to')
				}
			});
		}

		return items;
	}

	data.isLoading = true;

	if (input) {
		data.items = getListItems();
		data.isLoading = false;
	}

})();
